var search_results = workflowContext.actions.Vector_Search.outputs.body;
var system_message = workflowContext.actions.system_message.outputs;

var sources = ""
for (let i=0;i<search_results.length;i++)
{
    sources = sources + "\n" + search_results[i]['documentName'] +":" + search_results[i]['content']
}

var system_message = system_message + "\n" + "Sources: \n" + sources

return system_message

var system_message=workflowContext.actions.system_message_for_search_query.outputs;
var samples=workflowContext.actions.sample_responses.outputs
var original_user_query = workflowContext.trigger.outputs.body.prompt

var messages = [];
messages.push({role: "system", message: system_message});
for (var i=0; i<samples.length; i++)
{
    messages.push(samples[i]);
}

var user_message = "Generate search query for: " + original_user_query;
messages.push({ role: "user", message: user_message});

return messages;
